try {
  // ✅ Nombres EXACTOS de tus nodos (según tu captura)
  const candidateNodes = ['DefinirListaCampos', 'DefinirListaCampos1', 'DefinirListaCampos2', 'DefinirListaCampos3', 'DefinirListaCampos4'];

  const safeFirst = (nodeName) => {
    try {
      return $(nodeName).first(); // puede fallar si el nodo no está disponible en esta ejecución
    } catch (e) {
      return undefined;
    }
  };

  const getListaCampos = () => {
    // A) Si llega en el input del nodo actual
    const fromInput = $input.first()?.json?.ListaCampos;
    if (fromInput) return fromInput;

    // B) Si no, busca el primero disponible entre los 3 nodos
    for (const name of candidateNodes) {
      const n = safeFirst(name);
      const v = n?.json?.ListaCampos;
      if (v) return v;
    }
    return '';
  };

  // Quita comillas externas repetidas: "'a,b,c'" -> a,b,c  o  "\"a,b\"" -> a,b
  const stripOuterQuotes = (s) => {
    let out = String(s ?? '').trim();
    while (
      (out.startsWith("'") && out.endsWith("'")) ||
      (out.startsWith('"') && out.endsWith('"'))
    ) {
      out = out.slice(1, -1).trim();
    }
    return out;
  };

  // Convierte ListaCampos a array (soporta CSV, ;, saltos de línea, o JSON array en string)
  const parseListaCampos = (v) => {
    if (Array.isArray(v)) return v;
    const s0 = stripOuterQuotes(v);
    if (!s0) return [];

    if (s0.startsWith('[')) {
      try {
        const parsed = JSON.parse(s0);
        if (Array.isArray(parsed)) return parsed;
      } catch (_) {}
    }

    return s0
      .split(/[,;\n\r\t]+/)
      .map(x => x.trim())
      .filter(Boolean);
  };

  const listaCamposRaw = getListaCampos();
  const expectedHeaders = parseListaCampos(listaCamposRaw);

  const norm = (s) => String(s ?? '').trim().toLowerCase();

  const items = $input.all().map(i => i.json);
  if (items.length === 0) {
    return [{ json: { ok: false, message: 'No se detectaron filas en el Excel.' } }];
  }

  if (expectedHeaders.length === 0) {
    return [{
      json: {
        ok: false,
        message: 'ListaCampos está vacío o no llegó desde ninguno de los nodos DefinirListaCampos*.',
        debug: { listaCamposRaw }
      }
    }];
  }

  // ✅ Binario: hacerlo “safe” por si ese nodo no está en la rama
  let originalBinary;
  try {
    originalBinary = $('CargarArchivo').first()?.binary?.Archivo;
  } catch (e) {
    originalBinary = undefined;
  }

  // Detectar headers reales
  let actualHeaders = [];

  if (items[0]?.row && typeof items[0].row === 'object') {
    const keys = Object.keys(items[0].row);
    const numericKeys = keys.length > 0 && keys.every(k => /^\d+$/.test(k));
    if (numericKeys) {
      actualHeaders = Object.values(items[0].row).map(norm);
    } else {
      actualHeaders = Object.keys(items[0].row).map(norm);
    }
  } else {
    actualHeaders = Object.keys(items[0] ?? {}).map(norm);
  }

  const expectedNorm = expectedHeaders.map(norm);
  const actualSet = new Set(actualHeaders);
  const expectedSet = new Set(expectedNorm);

  const missing = expectedNorm.filter(h => !actualSet.has(h));
  const extra = actualHeaders.filter(h => !expectedSet.has(h));

  const ok = missing.length === 0 && extra.length === 0;

  return [{
    json: {
      ok,
      expected: expectedNorm,
      actual: actualHeaders,
      missing,
      extra,
      message: ok
        ? 'El archivo tiene exactamente los campos esperados.'
        : 'Los campos no coinciden exactamente con la lista esperada.',
      debug: { listaCamposRaw } // útil para ver de dónde vino
    },
    binary: originalBinary ? { Archivo: originalBinary } : {}
  }];

} catch (e) {
  // ✅ Garantiza que SIEMPRE se devuelva un array de items
  return [{
    json: {
      ok: false,
      message: 'Error en Validar Columnas (Code node).',
      error: String(e?.message ?? e),
      stack: e?.stack,
    }
  }];
}
