const BLNBR = query.BLNBR || '';
const CONTAINERNBR = query.CONTAINERNBR || '';

// Validar parámetros
if (!BLNBR || !CONTAINERNBR) {
  return JSON.stringify({
    error: 'Faltan parámetros requeridos',
    recibido: { BLNBR, CONTAINERNBR }
  });
}

// Construir SOAP body
const soapBody = `<soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" xmlns:tem="http://tempuri.org/">
  <soapenv:Header/>
  <soapenv:Body>
    <tem:GetTest>
      <tem:BLNBR>${BLNBR}</tem:BLNBR>
      <tem:CONTAINERNBR>${CONTAINERNBR}</tem:CONTAINERNBR>
    </tem:GetTest>
  </soapenv:Body>
</soapenv:Envelope>`;

// Llamada SOAP
try {
  const response = await this.helpers.httpRequest({
    method: 'POST',
    url: 'http://10.208.101.92:8029/WebServices/Querys.asmx',
    headers: {
      'Content-Type': 'text/xml; charset=utf-8',
      'SOAPAction': 'http://tempuri.org/GetTest'
    },
    body: soapBody
  });
  return response;

} catch (error) {
  return JSON.stringify({
    error: 'Error al llamar al servicio SOAP',
    mensaje: error.message,
    parametros_enviados: { BLNBR, CONTAINERNBR }
  }, null, 2);
}

